notes= """

"""
repeat = 1000
limit = 1000
segment = notes
notes = ""
for _ in range(3):
    notes += segment

print(notes)
possible_pairs_init = 0
for index, letter in enumerate(notes[:len(segment)]):
    if letter.islower():
        novice = letter
        for profesional in notes[max(index-limit, 0):min(index+limit+1, len(notes))]:
            if profesional.isupper() and profesional.lower() == letter:
                possible_pairs_init += 1


possible_pairs_2 = 0

for index, letter in enumerate(notes[: len(segment)*2]):
    if letter.islower():
        novice = letter
        for profesional in notes[max(index-limit, 0):min(index+limit+1, len(notes))]:
            if profesional.isupper() and profesional.lower() == letter:
                possible_pairs_2 += 1

possible_pairs_end = possible_pairs_init
possible_pairs_n = possible_pairs_2 - possible_pairs_init
